import { useState } from "react";

const DiaryEditor = () => {
    //useState("") : 초기값!!
    // const[state, setState] = useState();
    // const [author, setAuthor] = useState("이예나"); 를 하나로 합혀서!
    const [state, setState] = useState({
        author: "이예나",
        content: "",
        emotion: 1
    });

    const handleChangeState = (e) => {
        setState({
            ...state, //나머지는 그대로
            [e.target.name]: e.target.value //input의 이름을 author로 설정 -> author : e.trarget.name
        })
    }

    const handleSubmit = () => {
        if (state.author.length < 1) {
            alert("작성자는 최소 1글자 이상 입력해주세요");
        }
        alert("저장 성공");
    }

    return <div className="DiaryEditor">
        <h2>오늘의 일기</h2>
        <div>
            {/* onChange : callback함수, 값이 바뀌었을 때(event가 발생했을 때) 수행하는 함수
                            e(이벤트 객체)를 인자로 받음 */}
            <input
                name="author"
                value={state.author}
                onChange   //={(e) => {
                // setState({ // 객 체의 값을 바꾸려면 객체를 만들어 전달
                //     author: e.target.value, //author 값은 바꿈
                //     content: state.content  //content 값은 유지
                // })
                //}
                ={handleChangeState}

            //author 값을 바꿈
            >
            </input>
        </div>
        <div>
            {/* 여러 줄 입력 */}
            <textarea name="content" value={state.content} onChange={
                (e) => {
                    setState({
                        ...state, //스프레드 연산자 : 나머지는 그대로 유지!
                        content: e.target.value
                    })
                }
            } ></textarea>
        </div>
        <div>
            오늘의 감정점수 :
            <select name="emotion" value={state.emotion} onChange={handleChangeState}>
                <option value={1}>1</option>
                <option value={2}>2</option>
                <option value={3}>3</option>
                <option value={4}>4</option>
                <option value={5}>5</option>
            </select>
        </div>
        <div>
            <button onClick={handleSubmit}>일기 저장하기</button>
        </div>
    </div >

};

export default DiaryEditor;
