import { useState } from "react";

const DiaryEditor = () => {
    //useState("") : 초기값!!
    const [author, setAuthor] = useState("이예나");
    const [content, setContent] = useState("");

    return <div className="DiaryEditor">
        <h2>오늘의 일기</h2>
        <div>
            {/* onChange : callback함수, 값이 바뀌었을 때(event가 발생했을 때) 수행하는 함수
                            e(이벤트 객체)를 인자로 받음 */}
            <input value={author} onChange={
                (e) => { setAuthor(e.target.value) }
                //author 값을 바꿈
            }>
            </input>
        </div>
        <div>
            {/* 여러 줄 입력 */}
            <textarea value={content} onChange={
                (e) => { setContent(e.target.value) }
            } ></textarea>
        </div>
    </div>

};

export default DiaryEditor;
